mapper.update:
UPDATE fcjy_clf_zjjgxy
SET
    ZT = '301',
    CXSJ = #{date}
WHERE
    jgid = #{jgid}

UPDATE fcjy_clf_zjjgxy
SET
    ZT = #{zt}
WHERE
    jgid = #{jgid}

mapper.select:
select f2.qymc as "bldw",f3.dgfkyhmc as "jgyh",f4.fwmj as "jzmj",f1.htzje as "cjje",f1.zjgje as "jgje",f3.je as "rzje",f6.je as "zqje" from FCJY_CLF_ZJJGXY f1 join FCJY_CYZT_CYQYJBXX f2 on f1.jgfwjgbh = f2.qyid join FCJY_CLF_ZJJGRZJL f3 on f1.jgid = f3.jgid join FCJY_CLF_ZJJGHT f4 on f1.jgid = f4.jgid join FCJY_CLF_ZJJGCZJL f6 on f1.jgid = f6.jgid
to_char(f1.qdxyrq,'yyyy-mm-dd') = #{searchJyrq}
and to_char(f1.gdsj,'yyyy-mm-dd') = #{searchJsrq}
and f3.dgfkyhmc like '%${searchJgyh}%'
order by f1.jgbh

mapper.update:
UPDATE fcjy_clf_zjjgxy
